package advent2016

import (
  "fmt"
)

var d2input = "\\\\"

func Day2a() {
  keypad := [][]int{[]int{1, 2, 3}, []int{4, 5, 6}, []int{7, 8, 9}}
  x, y := 1,1
  ans := make([]int, 0)
  for _, c := range(d2input) {
    switch c {
      case 'L':
        if x - 1 < 0 {
        } else {
          x -= 1
        }
      case 'R':
        if x + 1 > 2 {
        } else {
          x += 1
        }
      case 'U':
        if y - 1 < 0 {
        } else {
          y -= 1
        }
      case 'D':
        if y + 1 > 2 {
        } else {
          y += 1
        }
      case '\n':
        ans = append(ans, keypad[y][x])
    }
  }
  ans = append(ans, keypad[y][x]) // last number not being printed
  for _, a := range ans {
    fmt.Printf("%v", a)
  }
}
